<?phpif ( $id > 0 ) { // $id = attribute name id!	// Including DAL (data access layer)	include_once '../config/database.php';	include_once '../library/db.php';	$query = "SELECT _id, value FROM attributes_values WHERE name_id=$id ORDER BY value";	$values = $db->query($query);	if ( $values->rowCount() <= 0 ) {		die("<option value=0>Questo attributo non ha valori.</option>");	}}?><option value=0>Seleziona valore...</option><?php while ( $value = $values->fetchObject() ) { ?>	<option value=<?=$value->_id?>><?=$value->value?></option><?php }