<h3>Ricerca Veloce</h3><?php$contracts_db = $db->query("SELECT * FROM contracts");$types_db = $db->query("SELECT * FROM types ORDER BY name ASC");$comuni_db = $db->query("SELECT * FROM comuni ORDER BY nome ASC");$frazioni_db = $db->query("SELECT * FROM frazioni WHERE id_comune={$id_comune} ORDER BY nome ASC");?><form id=filters method=POST>	<fieldset>			<input id=page name=page type=hidden value=<?=$page?>>			<p class=clearfix>			<label for=type_id>Tipologia</label>			<br>			<select id=type_id name=type_id>			<option value=0>Tutte le tipologie</option>						<?php while ( $type = $types_db->fetchObject() ) { ?>				<option value="<?=$type->_id?>"<?=($type->_id == $type_id) ? ' selected' : ''?>><?=$type->name?></option>			<?php } ?>			</select>		</p>			<p class=clearfix>			<label for=id_comune>Comune</label>			<br>			<select id=id_comune name=id_comune>				<option value=0>Tutti i comuni</option>			<?php while ( $comune = $comuni_db->fetchObject() ) { ?>				<option value="<?=$comune->_id?>"<?=($comune->_id == $id_comune) ? ' selected' : ''?>><?=$comune->nome?></option>			<?php } ?>			</select>		</p>					<p class=clearfix>			<label for=id_frazione>Località</label>			<br>			<select id=id_frazione name=id_frazione>				<option value=0>Tutte le località</option>			<?php if ( $frazioni_db->rowCount() > 0 ) { ?>				<?php while ( $frazione = $frazioni_db->fetchObject() ) { ?>					<option value="<?=$frazione->_id?>"<?=($frazione->_id == $id_frazione) ? ' selected' : ''?>><?=$frazione->nome?></option>				<?php } ?>			<?php } ?>			</select>		</p>				<p class=clearfix>			<label for=min_price>Prezzo min</label>			<br>			<input id=min_price type=text name=min_price value=<?=$min_price?> size=10 maxlength=10 dir=rtl> <small>&nbsp;&euro;</small>		</p>		<p class=clearfix>			<label for=max_price>Prezzo max</label>			<br>			<input id=max_price type=text name=max_price value=<?=$max_price?> size=10 maxlength=10 dir=rtl> <small>&nbsp;&euro;</small>		</p>		<p class=clearfix>			<label for=min_surface>Metri quadri min</label>			<br>			<input id=min_surface type=text name=min_surface value=<?=$min_surface?> size=4 maxlength=4>		</p>		<p class=clearfix>			<label for=max_surface>Metri quadri max</label>			<br>			<input id=max_surface type=text name=max_surface value=<?=$max_surface?> size=4 maxlength=4>		</p>		<p class=clearfix>			<input id=isUnderConstruction type=checkbox name=isUnderConstruction<?=$isUnderConstruction ? ' checked' : ''?>>			<label for=isUnderConstruction>Mostra solo i cantieri</label>					</p>				<!-- inutile, il form non verrà mai submittato: <input type=submit name=filters_submit value=Cerca> -->			</fieldset></form>