<?php/* only for positive values */function getElapsedTimeExtended ($seconds) {	$units = array(		31556926 => array('anno', 'anni'),		2629744 => array('mese', 'mesi'),			604800 => array('settimana', 'settimane'),		86400 => array('giorno', 'giorni'),		3600 => array ('ora', 'ore'),		60 => array('minuto', 'minuti'),		1 => array ('secondo', 'secondi')	);	$result = array();	foreach($units as $divisor => $unitName) {		$units = intval($seconds / $divisor);		if ($units) {			$seconds %= $divisor;			$name = $units == 1 ? $unitName[0] : $unitName[1];			$result[] = "$units $name";		}	}	return implode(', ', $result); // es: 2 settimane, 3 giorni, 5 ore, 2 minuti, 1 secondo}