<?php/* questa funzione Ã¨ una copia della funzione Table->getNumberOfRowsFromResult(),riadattata per la seconda versione di bluimmobiliareperugia.it */	/** Get the total number of rows from the query result, but without limit (exactly as phpmyadmin does). 	* @return An integer representing the number of records from the result.	*/		/* db = istanza del db; query = la query da eseguire (senza LIMIT, naturalmente...) */	function getNumberOfRowsFromResult ($db, $query) {		$subject = $this->query;		$search = ' LIMIT ';		$replace = '; --';		$query = str_replace($search, $replace, $subject);		$statement = $this->db->query($query);		return $statement->rowCount();	}