<?php/* spiegazione funzionamento:gli devi passare la differenza, in secondi, tra una data futura e la data odierna.21/02/2012*/function getRemainingTime ($differenceInSeconds) {	$units = array(		31556926 => array('anno', 'anni'),		2629744 => array('mese', 'mesi'),		604800 => array('settimana', 'settimane'),		86400 => array('giorno', 'giorni'),		3600 => array ('ora', 'ore'),		60 => array('minuto', 'minuti'),		1 => array ('secondo', 'secondi')	);	foreach($units as $divisor => $unitName) {		$units = intval($differenceInSeconds / $divisor);		if ($units) {			$differenceInSeconds %= $divisor;			$name = $units == -1 ? $unitName[0] : $unitName[1];			if ($units != 0)					return "$units $name"; // ritorna tipo 4 settimane, oppure 2 giorni, e basta		}	}		return false;}